mapper.select:
select commid,commname,commdesc,updatetime,category,commstatus
from commodity

    and commstatus = #{commstatus}
    and userid = #{userid}

order by updatetime desc limit #{page},#{count}

select count(*) from commodity

    and commstatus = #{commstatus}
    and userid = #{userid}

select commid,commname,orimoney,thinkmoney,school,updatetime,image
from commodity

    and category = #{category}
    and thinkmoney between #{minmoney} and #{maxmoney}
    and school != #{school}
    and school = #{school}
    and commstatus=1

order by updatetime desc limit #{page},#{count}

select count(*) from commodity

    and category = #{category}
    and thinkmoney >= #{minmoney}
    and thinkmoney <= #{maxmoney}
    and school != #{school}
    and school = #{school}
    and commstatus=1
